import React from 'react'; 
import Options from './Options/Options'
import Header from './Header/Header'; 
import Message from './Message/Message'; 

class New extends React.Component {
    constructor() {
        super() 
        this.state = {
            username: 'Daria',
            PlayerChoice:"smth",
            computerChoice: "other",
            firstPlayerScore: 0,
            computerScore: 0,
            startGame:false,
            endGame:false,
            tieGame:false,
            test:true,
            generateComputerChoiceBool:false,
            message:"Welcome to the game!"
          }

    }

    playerMoved = (choice) => {
        this.setState( {
                PlayerChoice: choice,
                startGame:true,
                test:true,
            })
        this.generateComputerChoice()
        this.broadcastTest()
    }

    generateComputerChoice() {
        var random = Math.floor(Math.random()*3); 
        let choices = ["rock","paper","scissors"]; 
        var computerChoice = choices[random]; 
        this.setState({
            computerChoice: computerChoice
        })
        console.log(`computer choice is ${computerChoice}`)

    }

    findWinner() {
        let resultArray = []; 
        resultArray.push(this.state.PlayerChoice) 
        resultArray.push(this.state.computerChoice) 
        let broadcast = ""; 

        if(resultArray[0] === resultArray[1]) {
            console.log("tie ")
            broadcast = "tie"
        }
        switch(resultArray[0],resultArray[1]) {
          case 'rock','paper':
            console.log(resultArray)
            console.log("Paper wins rock!")
            broadcast = "tie"
            break;
          case 'rock','scissors':
            console.log(resultArray)
            console.log("Scissors wins paper!")
            broadcast = "tie"
            break;
          case 'paper','rock':
            console.log(resultArray)
            console.log("Paper wins rock!") 
            broadcast = "tie"
            break;
          case 'paper', "scissors":
            console.log(resultArray)
            console.log("Rock wins scissors!")
            broadcast = "tie"
            break;
          case 'scissors', "rock":
            console.log(resultArray)
            console.log("Rock wins scissors!")
            broadcast = "tie"
            break;
          case 'scissors', "paper":
            console.log(resultArray)
            console.log("Rock wins scissors!")
            broadcast = "tie"
            break;
          default:
              // code block

    }

    broadcastTest = (broadcast) => {
        this.setState({
            message: broadcast
        })
    }
}
    render() {
        let message = null; 

        if(this.state.message) {
            message = (
              <div>
                 <Message message={this.state.message}> </Message> 
              </div>
            )
          }

        if(this.state.startGame) {
            this.findWinner()
        }


        return (
            <div>
                <Header> </Header>
                {message}
                <div className="flex-container">
                <Options click={() => this.playerMoved("paper")} choice="Paper" />
                <Options click={() =>this.playerMoved("rock")} choice="Rock" />
                <Options click={() =>this.playerMoved("scissors")} choice="Scissors" /> 
                </div>
            </div>
        )}
}

export default New;
