import React from 'react'; 
import Options from './Options/Options'

class New extends React.Component {
    constructor() {
        super() 
        this.state = {
            username: 'Daria',
            PlayerChoice:"smth",
            computerChoice: "other",
            firstPlayerScore: 0,
            computerScore: 0,
            startGame:false,
            endGame:false,
            tieGame:false,
            test:true,
            generateComputerChoiceBool:false
          }
        //   this.playerMoved = this.playerMoved.bind(this)
    }

    playerMoved = (choice) => {
        this.setState( {
                PlayerChoice: choice,
                startGame:true,
                test:true,
            })
    }

    generateComputerChoice() {
        var random = Math.floor(Math.random()*4); 
        let choices = ["rock","paper","scissors"]; 
        var computerChoice1 = choices[random]; 
        this.setState({
            computerChoice: computerChoice1
        })
        console.log(`computer choice is ${computerChoice1}`)
    }

    findWinner() {
        if(this.state.computerChoice === this.state.PlayerChoice) {
            console.log("tie ")
        }
        console.log("winner ")
    }


    render() {
        // var PlayerChoice = this.state.PlayerChoice; 
        // var computerChoice = this.state.computerChoice; 


        // console.log(`this is a computer choice ${computerChoice}`)
        // console.log(`this is playchoice ${PlayerChoice}`)

        // if(this.state.generateComputerChoiceBool) {
        //     this.generateComputerChoice()
        // }

        if(this.state.tieGame) {
            console.log("tie")
        }
        if(this.state.test) {
            console.log("test is working!")
        }

        if(this.state.startGame) {
            this.findWinner()
        }


        return (
            <div>
                <Options click={() => this.playerMoved("paper")} choice="Paper" />
                <Options click={() =>this.playerMoved("rock")} choice="Rock" />
                <Options click={() =>this.playerMoved("scissors")} choice="Scissors" /> 
            </div>
        )
    }
}

export default New;
