import React from 'react'; 

class New extends React.Component {
    constructor() {
        super() 
        this.state = {
            username: 'Daria',
            PlayerChoice:"smth",
            computerChoice:"other",
            firstPlayerScore: 0,
            computerScore: 0,
            startGame:false,
            endGame:false,
            tieGame:false,
            test:false 
          }
        //   this.playerMoved = this.playerMoved.bind(this)
    }

    playerMoved = () => {
        this.setState( {
                PlayerChoice: "rock",
                computerChoice:"rock",
                startGame:true
            })
    }

    if(endGame) {
        if(PlayerChoice === computerChoice) {
            this.setState = ({
                tieGame:true,
                test:true
            })
        }
        console.log(`this is end of game ${PlayerChoice}`)
    }
    // endOfTheGame() {
    //     this.setState = {
    //         tieGame:true,
    //         test:true
    //     }
    // }


    render() {
        var PlayerChoice = this.state.PlayerChoice; 
        var computerChoice = this.state.computerChoice; 
        let choices = ["rock","paper","scissors"]; 

        if(this.state.startGame) {
            // create random variable between 1 to 3 choosing between rock,paper, and sci) 
            let randNum = 0; 
            this.setState = ({
                computerChoice: choices[randNum],
                endGame: true,
                test:true
            })
            console.log("starting game")
        }


        console.log(`this is a computer choice ${computerChoice}`)
        console.log(`this is play choice ${PlayerChoice.toString()}`)

        // if(this.state.tieGame) {
        //     console.log("tie")
        // }
        if(this.state.test) {
            console.log("test is working!")
        }


        return (
            <div>
                <p onClick={() => this.playerMoved()}> Rock </p>
            </div>
        )
    }
}

export default New;
