            computerChoice:"rock",
            firstPlayerScore: 0,
            computerScore: 0,
            resultOfTheGame: false,
            startGame:false,
            endGame:false,
            tieGame:false,
          }
        //   this.playerMoved = this.playerMoved.bind(this)
    }

    playerMoved = () => {
        this.setState(
            {
