import React from 'react'; 

class New extends React.Component {
    constructor() {
        super() 
        this.state = {
            username: 'Daria',
            PlayerChoice:"smth",
            computerChoice:"rock",
            firstPlayerScore: 0,
            computerScore: 0,
            startGame:false,
            endGame:false
          }
        //   this.playerMoved = this.playerMoved.bind(this)
    }

    playerMoved = () => {
        this.setState(
            {
                PlayerChoice: "rock",
                startGame:true,
                endGame:true
            }
        )
    }


    render() {
        var PlayerChoice = this.state.PlayerChoice; 
        var computerChoice = this.state.computerChoice; 
        let choices = ["rock","paper","scissors"]; 

        if(this.state.startGame) {
            // create random variable between 1 to 3 choosing between rock,paper, and sci) 
            let randNum = 0; 
            this.setState = {
                computerChoice: choices[randNum],
                endGame: true
            }
        }


        // console.log(`this is a computer choice ${computerChoice}`)
        // console.log(`this is play choice ${PlayerChoice}`)

        if(this.state.endGame) {
            if(this.state.PlayerChoice === this.state.computerChoice) {
                console.log("working!")
            }
            console.log(`this is end of game ${PlayerChoice}`)
        }

        return (
            <div>
                <p onClick={this.playerMoved}> Rock </p>
            </div>
        )
    }
}

export default New;
